import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import vietnam from '../../images/vietnam-3.png';
import star from '../../images/star1.png';
import "./LeftSide.css";

let bookList = [
  {'title':'ok','author':'ernest','pages':2},
  {'title':'ok2','author':'ernest2','pages':22},
  {'title':'ok3','author':'ernest3','pages':32},
]

// const Book = ({title, author, pages}) => {
//   return (
//     <section>
//       <h2>{title}</h2>
//       <p>{author}</p>
//       <p>{pages}</p>
//     </section>
//   )
// }
const destinationArr = [
  {'title':'SAIGON','para':'ernest'},
  {'title':'DALAT','para':'ernest2'},
  {'title':'NHA TRANG','para':'ernest3'},
  {'title':'DA NANG','para':'ernest3'},
  {'title':'HUE','para':'ernest3'},
  {'title':'HANOI','para':'ernest3'},
]

class RightSide extends React.Component {
  constructor(props) {
                // Required to call original constructor
                super(props);
                // Props are now accessible from here
                var v = props.reactProp;
            }
  render() {
  return (
    <div>
      <h1 className="right-h1">{this.props.reactProp}</h1>
      <p>{this.props.reactProp2}</p>
      <p>Click on other destinations to find out more!</p>
    </div>
    )
  }
}

class leftSide extends Component {
    chooseSaigon = () => {
      ReactDOM.render(<RightSide reactProp={destinationArr[0].title} reactProp2={destinationArr[0].para} />, document.getElementById('right'));
    }
    chooseDalat = () => {
      ReactDOM.render(<RightSide reactProp={destinationArr[1].title} reactProp2={destinationArr[1].para} />, document.getElementById('right'));
    }
    chooseNhatrang = () => {
      ReactDOM.render(<RightSide reactProp={destinationArr[2].title} reactProp2={destinationArr[2].para} />, document.getElementById('right'));
    }
    chooseDanang = () => {
      ReactDOM.render(<RightSide reactProp={destinationArr[3].title} reactProp2={destinationArr[3].para} />, document.getElementById('right'));
    }
    chooseHue = () => {
      ReactDOM.render(<RightSide reactProp={destinationArr[4].title} reactProp2={destinationArr[4].para} />, document.getElementById('right'));
    }
    chooseHanoi = () => {
      ReactDOM.render(<RightSide reactProp={destinationArr[5].title} reactProp2={destinationArr[5].para} />, document.getElementById('right'));
    }
    render() {
      return (
        <div className="left-div">
            <h1 className="main-header">VIETNAM</h1>
            <img className="vietnam" src={vietnam} /> 
            <button className="button1" onClick={this.chooseSaigon}><img className="star" src={star} /></button>
            <button className="button1" onClick={this.chooseDalat}><img className="star2" src={star} /></button>
            <button className="button1" onClick={this.chooseNhatrang}><img className="star3" src={star} /></button>
            <button className="button1" onClick={this.chooseDanang}><img className="star4" src={star} /></button>
            <button className="button1" onClick={this.chooseHue}><img className="star5" src={star} /></button>
            <button className="button1" onClick={this.chooseHanoi}><img className="star6" src={star} /></button>
        </div>
      )
    }
  }



export default leftSide;
