const destinationArr = [
  {'title':'HANOI','para':'ernest'},
  {'title':'ok2','para':'ernest2'},
  {'title':'ok3','para':'ernest3'},
]

class RightSide extends React.Component {
  constructor(props) {
                // Required to call original constructor
                super(props);
                // Props are now accessible from here
                var v = props.reactProp;
            }
  render() {
  return (
    <div>
      <h1 className="right-h1">{this.props.reactProp}</h1>
      <p>{this.props.reactProp2}</p>
      <p>Click on other destinations to find out more!</p>
    </div>
    )
  }
}

class leftSide extends Component {
    chooseHanoi = () => {
      ReactDOM.render(<RightSide reactProp={destinationArr[0].title} reactProp2={destinationArr[0].para} />, document.getElementById('right'));
    }
    render() {
      return (
        <div className="left-div">
            <h1 className="main-header">VIETNAM</h1>
            <img className="vietnam" src={vietnam} /> 
            <button className="button1" onClick={this.chooseHanoi}><img className="star" src={star} /></button>
        </div>
      )
    }
  }



export default leftSide;
