import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import vietnam from '../../images/vietnam-3.png';
import star from '../../images/star1.png';
import "./LeftSide.css";

let bookList = [
  {'title':'ok','author':'ernest','pages':2},
  {'title':'ok2','author':'ernest2','pages':22},
  {'title':'ok3','author':'ernest3','pages':32},
]

const Book = ({title, author, pages}) => {
  return (
    <section>
      <h2>{title}</h2>
      <p>{author}</p>
      <p>{pages}</p>
    </section>
  )
}

// class RightSide extends React.Component {
//   render() {
//     return <h2>Hi, I am a Car!</h2>;
//   }
// }

class leftSide extends Component {
    chooseDestination = () => {
      // document.getElementById('right').innerHTML("hi")
      // alert("hi")
      ReactDOM.render(<Book title={bookList.title[0]} author={bookList.author[0]} pages={bookList.title[0]} />, document.getElementById('right'));
    }
    render() {
      return (
        <div className="left-div">
            <h1 className="main-header">VIETNAM</h1>
            <img className="vietnam" src={vietnam} /> 
            <button className="button1" onClick={this.chooseDestination}><img className="star" src={star} /></button>
        </div>
      )
    }
  }



export default leftSide;
